class Solution {

    public ListNode insertionSortList(ListNode head) {
        if(head==null)
            return null;
        if(head.next==null)
            return head;
        ListNode ptr;
        int c=0,i,j=0;
        for(ptr=head;ptr!=null;ptr=ptr.next)
        {
            c++;
        }
        int a[]=new int[c];
        a[0]=head.val;
        for(ptr=head.next,i=1;ptr!=null;ptr=ptr.next,i++)
        {
            j=i-1;
            while(j>=0&&ptr.val<a[j])
            {
                a[j+1]=a[j];
                j--;
            }
            a[j+1]=ptr.val;
        }
        i=0;
        for(ptr=head;ptr!=null;ptr=ptr.next)
        {
            ptr.val=a[i++];
        }
        return head;
    }
}

