        head -> next = NULL;
        ListNode *sortedListHead = insertionSortList(nextFromHead);

        int valueToInsert = head -> val;

        if(!sortedListHead or sortedListHead -> val >= valueToInsert) {
            head -> next = sortedListHead;
            return head;
        }

        ListNode *curr = sortedListHead, *prev = NULL;
        while(curr and curr -> val < valueToInsert) {
            prev = curr;
            curr = curr -> next;
        }

        prev -> next = head;
        head -> next = curr;
        return sortedListHead;
    }
};
```

