class Solution {
    public ListNode insertionSortList(ListNode head) {
        ListNode dummyHead = new ListNode();
        dummyHead.next = head;
        ListNode slow = dummyHead.next;
        ListNode fast = dummyHead.next.next;
        while (fast != null) {
            if (fast.val < slow.val) {
                // find out where to place
                ListNode insertAfter = dummyHead;
                while (insertAfter.next.val < fast.val) {
                    insertAfter = insertAfter.next;
                }
                // save temp values
                final ListNode recovery = slow;
                final ListNode tempFastNext = fast.next;
                final ListNode tempInsertAfterNext = insertAfter.next;
                // swap
                slow.next = tempFastNext;
                insertAfter.next = fast; 
                fast.next = tempInsertAfterNext; 
                // update next pointers
                slow = recovery;
                fast = recovery.next;
                continue;
            }
            slow = fast;
            fast = fast.next;
        }
        return dummyHead.next;
    }
}
