Thought process:
// iterate over the linked list, at each "node":
// if it is less than prev node:
//      connect prev to next
//      move this "node" to the correct position
// else: keep going
```
func insertionSortList(_ head: ListNode?) -> ListNode? {
	guard let head = head else { return nil }
	let dummy = ListNode(-1)
	dummy.next = head

	var prev = head
	var node = head.next
	while node != nil {
		if node!.val < prev.val {
			prev.next = node!.next
			insert(dummy, node!)
			node = prev.next
		} else {
			prev = node!
			node = node!.next
		}
	}
	return dummy.next
}

func insert(_ head: ListNode, _ insertingNode: ListNode) {
	var node = head
	while let next = node.next, insertingNode.val > next.val {
		node = next
	}
	let next = node.next
	node.next = insertingNode
	insertingNode.next = next
}
```

