            if (sorted)
            {
                // Inserted in the front of the sorted list
                if (cnt == 0)
                {
                    head = unsorted;
                }
                else
                {
                    preSorted->next = unsorted;
                }

                unsorted->next = sorted;

            }
            else
            {
                // Inserted in the end of the sorted list
                unsorted->next = NULL;
                preSorted->next = unsorted;
            }
            unsorted = next;
        }
        return head;
    }

