class Solution {
public:
    int bestClosingTime(string customers) {
        int n=customers.size();
        vector<int> pref(n+1,0);
        vector<int> suff(n+1,0);
        for(int i=1;i<=n;i++)
        {
          if(customers[i-1]=='N')
          pref[i]+=pref[i-1]+1;
          else
          pref[i]+=pref[i-1];
        }
        for(int i=n-1;i>=0;i--)
        {
            if(customers[i]=='Y')
            suff[i]+=(suff[i+1]+1);
            else
            suff[i]+=(suff[i+1]);
        }
        vector<int> ans(n+1);
        for(int i=0;i<=n;i++)
        {
            ans[i]=pref[i]+suff[i];
        }
        int x=*min_element(ans.begin(),ans.end());
        for(int i=0;i<=n;i++)
        {
            if(ans[i]==x)
            return i;
        }
        return -1;
    }
};
