class Solution {
public:
    int bestClosingTime(string cus) {
        int l = cus.length();
        int cY=0, cN=0;
        //counting Number of Y
        for(int i=0; i<l; i++){
            if(cus[i]=='Y'){
                cY++;
            }
        }
        int loss = INT_MAX;
        int ans =0;

        for(int i=0; i<l; i++){
            //counting total Number of penalty at ith time
            int x = cY+cN;
            if(loss > x){
                ans = i;
                loss = x;
            }
            //If a customer is coming in ith time then closing after ith time will not cause penalty
            if(cus[i]=='Y'){
                cY--;
            }else{
                //If no customer is coming in ith time then keep the shop open after ith time will cause penalty
                cN++;
            }
        }
        //Edge case
        int x = cY+cN;
        if(loss>x){
            ans  = l;
        }
        return ans;
    }
};
```

