        for(int i = n-1; i >= 0; i--){
           if(c[i] == 'Y'){
               prefix[i] = 1 + prefix[i+1];
           }
            else prefix[i] = prefix[i+1];
        }

        int suffix[n+1];
        suffix[0] = 0;

        for(int i = 1; i <= n; i++){
            if(c[i-1] == 'N'){
                suffix[i] = 1 + suffix[i-1];
