            if (s[i]=='N') {
                n.push_back(1);
            } else {
                n.push_back(0);
            }
        }
        // prefix sum for vector n
        for (int i=0; i<m; i++) {
            n[i+1]=n[i]+n[i+1];
        }
        // the purpose of pushing 0 at the beginning in y and at the end in n is
