class Solution {
public:
    int bestClosingTime(string customers) 
    {
        int n = customers.size();
        vector<vector<int>>pre(n, vector<int>(2, 0));
        pre[0][0] = (customers[0] == 'N')? 1 : 0;
        pre[0][1] = (customers[0] == 'Y')? 1 : 0;
        for (int i = 1; i < n; i++)
        {
            pre[i][0] = (customers[i] == 'N'? 1 : 0) + pre[i - 1][0];
            pre[i][1] = (customers[i] == 'Y'? 1 : 0) + pre[i - 1][1];
        }
        //================================================
        vector<vector<int>>post(n, vector<int>(2, 0));
        post[n - 1][0] = (customers[n - 1] == 'N')? 1 : 0;
        post[n - 1][1] = (customers[n - 1] == 'Y')? 1 : 0;
        for (int i = n - 2; i >= 0; i--)
        {
            post[i][0] = (customers[i] == 'N'? 1 : 0) + post[i + 1][0];
            post[i][1] = (customers[i] == 'Y'? 1 : 0) + post[i + 1][1];
        }
        //=====================================================
        int mnPenalty = n, ans = -1;
        for (int i = 0; i <= n; i++)
        {
            int prevPenalty = (i - 1 < 0)? 0 : pre[i - 1][0];
            int nextPenalty = (i == n)? 0 : post[i][1];
            int penalty = prevPenalty + nextPenalty;
            if (penalty < mnPenalty)
            {
                ans = i;
                mnPenalty = penalty;
            }
        }
        return ans;

    }
};
```

