class Solution {
public:
    int bestClosingTime(string s) {

        int n=s.size();
        vector<int>open_pre(n+1,0);

       for(int i=1;i<n+1;i++)
       {
           if(s[i-1]=='N')//shop is open and no customer the increase the penalty
           {
               open_pre[i]=open_pre[i-1]+1;
           }
           else{
               open_pre[i]=open_pre[i-1];
           }
       }
      vector<int>close_suf(n+1,0);

        for(int i=n-1;i>=0;i--)
        {
            if(s[i]=='Y')// shop is closed and there are customer than increase the penalty
            {
                close_suf[i]=close_suf[i+1]+1;
            }
            else{
                close_suf[i]=close_suf[i+1];
            }
        }
        int mn=INT_MAX;
        int ans;
        for(int i=0;i<n+1;i++)
        {
            if(i==0)
            {
              if(mn>close_suf[0])
