```
/*    
         penalty comes because of 2 reasons :-

         1) The shopkeeper remains idle for some h hours which increases penalty by h
         2) The number of customers that visited the shop after shop being closed

         Intuition:-

          -> Since the above two factors decide the penalty , we need to create 2 prefix array , one will tell the number of 'N' from left including the present index and one will tell the number of 'Y' to the right side of the present index;

*/
class Solution {
public:
    int bestClosingTime(string customers) {

        string s = "#" + customers;

        int n = s.length();

        vector<int> left(n,0);

        for(int i=1;i<n;i++){
            if(s[i]=='N'){
                left[i] = left[i-1]+1;
            }
            else{
                left[i] = left[i-1];
            }
        }

        vector<int> right(n,0);

        if(s[n-1]=='Y'){
            right[s.length()-1] = 1;
        }

        for(int i=n-2;i>=0;i--){
            if(s[i]=='Y'){
                right[i] = right[i+1]+1;
            }
            else{
                right[i] = right[i+1];
            }
        }


        vector<int> temp(n,0);

        for(int i=0;i<temp.size()-1;i++){
            temp[i] = left[i] + right[i+1];
        }

        temp[temp.size()-1] = left[temp.size()-1];


        int min_penalty = INT_MAX;

        int hour = -1;

        for(int i=0;i<temp.size();i++){

            if(temp[i]<min_penalty){
                min_penalty = temp[i];
                hour = i;
            }
        }

        return hour;

    }
};
```

