        int index  = -1;
        int minVal = INT_MAX;
        for(int i = 0 ; i <= n ; i++){
            if(Y[i] + N[i] < minVal){
                minVal = Y[i] + N[i];
                index = i;
            }
        }
        return index;
    }
};
