class Solution {
public:
    int bestClosingTime(string customers) {
        int y[customers.length()+1];
        int n[customers.length()+1];
        y[0]=0;
        n[0]=0;
        int ytill{0};
        int ntill{0};

        for(int i{0};i<customers.length();i++){
            if(customers[i]=='Y'){
                ytill++;
            }else{
                ntill++;
            }
            y[i+1]=ytill;
            n[i+1]=ntill;
        }

        int ind{0};
        for(int i{1};i<customers.length()+1;i++){
            //calculate penalty
            //Penalty = ytill - y[i] + n[i]
            if(ytill-y[ind]+n[ind]>ytill-y[i]+n[i]){
                ind=i;
            }
        }
        return ind;
    }
};
