class Solution:
    def bestClosingTime(self, customers: str) -> int:
        n = len(customers)
        dp = [0] * (n+1)
        dp[0] = customers.count('Y')
        for i in range(n):
            if customers[i] == 'N':
                dp[i+1] = dp[i] + 1
            else:
                dp[i+1] = dp[i] - 1
        return dp.index(min(dp))
