class Solution {
public:
    int bestClosingTime(string customers) {

        int count = 0;
        for(int i=0; i<customers.size(); i++)
        {
            if(customers[i]=='N')
            {
                count++;
            }
        }
        int max_idx = customers.size();
        int max_no = count;

        for(int i=customers.size()-1; i>=0; i--)
        {
            if(customers[i]=='N')
            {
                if(count!=0)
                {
                    count--;
                }
            }
            else
            {
                count++;
            }

            if(count<=max_no)
            {
                max_no = count;
                max_idx = i;
            }

        }

        return max_idx;

    }
};
```

