class Solution {
public:
    int bestClosingTime(string customers) {
        int n=customers.size();
        int pre[n];int temp=0;
        for(int i=0;i<n;i++){
            if(customers[i]=='N'){
                temp++;
            }
            pre[i]=temp;
        }
        int suff[n];temp=0;
        for(int i=n-1;i>=0;i--){
            if(customers[i]=='Y'){
                temp++;
            }
            suff[i]=temp;
        }int mini=pre[n-1];
        for(int i=0;i<n;i++){
            if(i==0){
               mini=min(mini,suff[i]);
            }
            else{
                mini=min(mini,pre[i-1]+suff[i]);
            }
        }
        for(int i=0;i<n;i++){
            if(i==0 && suff[i]==mini){
                return i;
            }
            else if(i!=0 && (mini==suff[i]+pre[i-1])){
                return i;
            }
        }
        return n;
    }
};
```

