class Solution {
public:
    int bestClosingTime(string customers) {
        int count_Y = 0, count_N = 0;
        for(int i=0;i<customers.size(); i++){
            if(customers[i] == 'Y') count_Y++;
        }
        int mini = 1e9, ind = -1;
        for(int i=0; i<customers.size(); i++){
            int temp = count_Y + count_N;
            //Checking greedyly if we could find someone less than
            //what we have selected ans just updating it
            if(mini > temp){
                mini = temp; ind = i;
            }
            (customers[i] == 'Y')? count_Y-- : count_N++;
        } 
        //Just to check for the last hour
        int temp = count_Y + count_N;
        if(mini > temp){
            mini = temp; ind = customers.size();
        }
        return ind;
    }
};
```

