        if(customers[0]=='N')
        prefixN[0]=1;
        for(int i=1; i<customers.size(); i++)
        {
            if(customers[i]=='N')
            prefixN[i]= 1+ prefixN[i-1];
            else
            prefixN[i]=prefixN[i-1];
        }

        //suffix
        if(customers[n-1]=='Y')
        suffixY[n-1]=1;
        for(int i=n-2; i>=0; i--)
        {
            if(customers[i]=='Y')
            suffixY[i]= 1+ suffixY[i+1];
            else
            suffixY[i]= suffixY[i+1];
