        int mini= 1e6;int res=-1;
        //calculate penalty
        for(int i=0; i<=n; i++)
        {
            int penalty= suffixY[i];
            if(i>0)
            penalty+= prefixN[i-1];
            if(penalty<mini)
            {
                mini= penalty;
                res=i;
            }
        }
        return res;
    }
};
