class Solution {
public:
    int bestClosingTime(string s) 
    {
        int n=s.size();
        vector<int>pre(n+5,0),suf(n+5,0);
        for(int i=1;i<=n;i++)
        {
            pre[i]=pre[i-1];
            if(s[i-1]=='N')pre[i]++;
        }
        for(int i=n-1;i>=0;i--)
        {
            suf[i]=suf[i+1];
            if(s[i]=='Y')suf[i]++;
        }
        int ans=-1,pen=INT_MAX;
        for(int i=0;i<=n;i++)
        {
            if(pre[i]+suf[i]<pen)
            {
                pen=pre[i]+suf[i];
                ans=i;
            }
        }
        return ans;
    }
};
