class Solution {
    public int bestClosingTime(String customers) {
         int n = customers.length();
        int[] a = new int[n + 1];
        for (int i = 0; i < n; i++) {
            if (customers.charAt(i) == 'Y') {
                a[i + 1] = a[i] + 1;
            } else {
                a[i + 1] = a[i];
            }
        }
        int min = n;
        int index = 0;
        int ball;
        for (int i = 0; i <= n; i++) {
            ball = (i - a[i]) + (a[n] - a[i]);
            if (min > ball) {
                min = ball;
                index = i;
                if (min == 0) return index;
            }
        }

        return index;
    }
