class Solution {
public:
    int bestClosingTime(string customers) {
        int yes = 0, no = 0;
        int ans = -1;
        int penalty = INT_MAX;
        int n = customers.size();
        for (int i = 0; i < n; ++i) {
            if (customers[i]== 'Y') yes++;
        }

        for (int i = 0; i <= n; ++i) {
            int prevInd = i - 1;
            if (prevInd >= 0) {
                if (customers[prevInd] == 'Y') yes--;
                else no++;
            }

            if (yes + no < penalty) {
                penalty = yes + no;
                ans = i;
            }
        }

        return ans;
    }
};
