class Solution {
public:
    int bestClosingTime(string customers) {
      priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>pq;
      int i=0;
      int sum=0;
      int n=customers.size();
      for(int i=0;i<n;i++){
          if(customers[i]=='Y'){
              sum+=1;
          }
      }
      pq.push({sum,0});
      while(i<n){
          if(customers[i]=='Y'){
              customers[i]='N';
              sum-=1;
              pq.push({sum,i+1});
          }
          else{
              customers[i]='Y';
              sum+=1;
              pq.push({sum,i+1});
          }
          i++;
      }
      int ans=pq.top().second;
      return ans;  
    }
};
