                alphabet[c - 'a']++;
            }
            for (byte freq : alphabet) {
                sb.append(freq).append(",");
            }
            String key = sb.toString();
            if (map.containsKey(key)) {
                map.get(key).add(word);
            } else {
                List<String> list = new ArrayList<>();
                list.add(word);
                map.put(key,list);
            }
            sb.setLength(0);
        }
        return new ArrayList<>(map.values());
    }
