impl Solution {
    pub fn group_anagrams(strs: Vec<String>) -> Vec<Vec<String>> {
        let mut out = std::collections::HashMap::new();
        for v in strs {
            let mut k: Vec<u8> = v.bytes().collect();
            k.sort_unstable();
            out.entry(k).or_insert_with(|| vec![]).push(v)
        }
        out.into_iter().map(|(_, v)| v).collect()
    }
}
