class Solution {
public:
    void count_sort(string& s){
        int n=s.size();
        vector<int>mp(26,0);
        string ans;
        for(int i=0;i<n;i++){
            mp[s[i]-'a']++;
        }
        for(int i=0;i<26;i++){
          ans.append(mp[i],i+'a');
        }
        s=ans;
    }
    vector<vector<string>> groupAnagrams(vector<string>& str) {

        map<string,vector<string>>mp;
        int k=0;
        for(auto x:str){
           string s=x;
           count_sort(s);
           mp[s].push_back(x);
        }
        vector<vector<string>>res;
        for(auto x:mp)
         res.push_back(x.second);
        return res;
    }
};
