class Solution
{
public:
    vector<vector<string>> groupAnagrams(vector<string> &strs)
    {
        unordered_map<string, vector<string>> hash; // hash[string] = {anagramic set of that string}
        vector<vector<string>> ans;
        for (auto str : strs)
        {
            string tmp = str;
            sort(begin(tmp), end(tmp));
            if (hash.find(tmp) != end(hash)) // If anagram of current string has been found previously
                hash[tmp].push_back(str); // Push current string into that anagramic set
            else
                hash.insert({tmp, {str}}); // Otherwise create a new entry into the hash map
        }
        for (auto [k, v] : hash)
            ans.push_back(v); // Push each anagramic set into the answer vector
        return ans;
    }
};
```
