    public boolean isSameArray(int[] arr1, int[] arr2) {
        for (int i = 0; i < arr1.length; i++) {
            if(arr1[i] != arr2[i]) {
                return false;
            }
        }
        return true;
    }

    public int[] countAlphabets(String s) {
        int[] output = new int[26];

        for (char c: s.toCharArray()) {
            output[c - 'a']++;
        }

        return output;
    }

    public List<List<String>> groupAnagrams(String[] strs) {
        Set<Integer> seenIndex = new HashSet<>();
        List<List<String>> output = new ArrayList<>();

        for (int i = 0; i < strs.length; i++) {
            String s = strs[i];

            if (seenIndex.contains(i)) {
                continue;
            }

            List<String> anagramGroup = new ArrayList<>();
            anagramGroup.add(s);
            seenIndex.add(Integer.valueOf(i));

            int parentAlphabets[] = countAlphabets(s);

            for (int j = i + 1; j < strs.length; j++) {
                String s2 = strs[j];

                int[] childAlphabets = countAlphabets(s2);

                if (isSameArray(parentAlphabets, childAlphabets)) {
                    anagramGroup.add(s2);
                    seenIndex.add(Integer.valueOf(j));
                }
            }

            output.add(anagramGroup);
        }

        return output;
    }
}
