            int temp=0;
            for(auto x:mp){
                temp = max(temp,x.second);
            }
            // temp store max points with same slope with respect to point i.
            // store the maximum value in ans.
            // total points in line will be temp+1 as 1 for the ith point also.
            ans = max(ans,temp+1);
        }
        return ans;
    }
};
