        vector<point> coordinates;
        for(auto& i:points){
            point k=point((double)i[0],(double)i[1]);
            coordinates.push_back(k);
        }
        int ans=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                int cnt=0;
                for(int k=0;k<n;k++){
                    if(i!=j&&j!=k&&i!=k){
                        if(isCollinear(coordinates[i],coordinates[j],coordinates[k])){
                            cnt++;
                        }
                    }
                }
                ans=max(cnt+2,ans);
            }
        }
        return ans;
    }
};
