        for(int i=0;i<n-1;i++){
            map<double,int> mp;
            for(int j=i+1;j<n;j++){
                double x2=pt[j][0],x1=pt[i][0];
                double y2=pt[j][1],y1=pt[i][1];
                double slope =(y2-y1)/(x2-x1);
                if(pt[j][1]-pt[i][1]<0 &&(pt[j][0]-pt[i][0])==0 ) 
