    }

    int maxPoints(vector<vector<int>>& points) {
        int n = points.size();
        if (n==1) return 1;// edge case;
        for (int i = 0; i < n-1; i++) {
            for (int j = i+1; j < n; j++) {
                line(points, i, j);
            }
        }

        int ans = 0;
        for (auto& [_, set] : L) {
            int v = set.size();
            ans = max(ans, v);
