            double slope = numeric_limits<double>::infinity();
            if (y[0] - x[0] != 0) {
                slope = (double) (y[1] - x[1]) / (y[0] - x[0]);
            }

            if (slopes.count(slope)) {
                slopes[slope]++;
            } else {
                slopes[slope] = 1;
            }
            maxi = std::max(maxi, slopes[slope]);
            }
        }
        return maxi + 1;
    }
};
