class Solution {
public:
    int maxPoints(vector<vector<int>>& points) {
        if (points.size()<=2){
            return points.size();
        }
        int overlap = 0, result = 0, cur = 0;
        int x, y, gcd;
        map<int, map<int, int>> freq;
        for (int i=0; i<points.size()-1; i++){
            overlap = 0; cur = 0; freq.clear();
            for (int j=i+1; j<points.size(); j++){
                x = points[j][0] - points[i][0];
                y = points[j][1] - points[i][1];
                if (x==0 && y==0){
                    overlap++; continue;
                }
                gcd = generateGCD(x, y);
                if (gcd != 0){
                    x /= gcd; y /= gcd;
                }
                freq[x][y]++;
                cur = max(cur, freq[x][y]);
            }
            result = max(result, cur + overlap + 1);
        }
        return result;
    }

    int generateGCD(int a, int b) {
        if (b == 0) return a;

        return generateGCD(b, a % b);
    }
};
