class Solution {
public:
    int maxPoints(vector<vector<int>>& pts) {
        if(pts.size() == 1) return 1;
        sort(pts.begin(), pts.end());
        int res = 2;
        for(int i = 0; i < pts.size()-1; i++) {
            unordered_map<double, int> mp;
            for(int j = i+1; j < pts.size(); j++) {
                double num = pts[j][1] - pts[i][1];
                double den = pts[j][0] - pts[i][0];
                if(j != i) mp[atan2(num, den)]++;
            } for(auto [k, v]: mp) res = max(res, v+1);
        } return res;
    }
};
