class Solution {
public:
    int maxPoints(vector<vector<int>>& points) {
            if(points.size()<=2)
                    return points.size();

            int ans=INT_MIN;

        for(int i=0;i<points.size();i++)
        {
                for(int j=i+1;j<points.size();j++)
                {
                        int dy=points[j][1]-points[i][1];
                        int dx=points[j][0]-points[i][0];
                                int count=0;

                        //count from index 0 to size-1 of vector similar slope
                        for(int k=0;k<points.size();k++)
                        {
                                // here we do product due to errors in precision after decimal
                                // for example, here in int when we divide two large number 
                                // then we got 0.00003 and 0.00004 but in (int) both are equal 
                                // so we use multiplication instead of divide

                                // (y-y1)(x1-x2)==(x-x1)(y1-y2)

                                int a=dx*(points[k][1]-points[i][1]);
                                int b=dy*(points[k][0]-points[i][0]);
                                if(a==b)
                                        count++;
                        }
