public class Solution {
    //用斜率来做
    //需要注意x为0的情况
    public int MaxPoints(int[][] points) {
        int result = 0;
        for(int i = 0; i < points.Length; i++)
        {
            Dictionary<double, int> dic = new Dictionary<double, int>();
            int max = 0;
            for(int j = i + 1; j < points.Length; j++)
            {
                double slope = this.GetSlope(points[i], points[j]);
                if(!dic.ContainsKey(slope))
                {
                    dic[slope] = 0;
                }

                dic[slope]++;
                max = Math.Max(max, dic[slope]);
            }

            result = Math.Max(result, max + 1);
        }

        return result;
    }

    private double GetSlope(int[] point1, int[] point2)
    {
        double dx = point2[0] - point1[0];
        double dy = point2[1] - point1[1];

        if (dx == 0)
        {
            return Double.MaxValue;
        }

        return dy/dx;
    }
}
