```
// y = kx + b
// for 2 points: (x1, y1), (x2, y2) we have a system:
// y1 = kx1 + b
// y2 = kx2 + b
// y1 - y2 = kx1 + b - (kx2 + b) = k*(x1 - x2)

// k = (y1 - y2) / (x1 - x2) = p / q
//
// y = kx + b => b = y - kx
// b = y1 - k * x1 = y1 - p/q * x1 = (q*y1 - x1*p) / q

// So in order to identify a line we must have:
// k and b where k = p/q, b = (q*y1 - x1*p) / q. 
//
// in our case it is enough to store: [p, q, (q*y1-x1*p)]

type Line struct {
    p, q, b int64
}

func maxPoints(points [][]int) int {
    xGroup := make(map[int]int)
    yGroup := make(map[int]int)
    res := 0

    for i := 0; i < len(points); i++ {
        x, y := points[i][0], points[i][1]
        xGroup[x]++
        yGroup[y]++

        res = max(max(res, xGroup[x]), yGroup[y])
    }

    for i := 0; i < len(points); i++ {
        // we reserve self point as a duplicate
        lines := map[Line]int{}
        duplicates := 1

        for j := i + 1; j < len(points); j++ {
            a, b := points[i], points[j]

            if a[0] == b[0] && a[1] == b[1] {
                duplicates++
            }
        }

        for j := i + 1; j < len(points); j++ {
            a, b := points[i], points[j]

            if a[0] == b[0] || a[1] == b[1] {
                continue
            }

            p, q := getSlope(a, b)
            line := Line{p: p, q: q, b: q * int64(a[1]) - p * int64(a[0])}

            lines[line]++
            res = max(res, lines[line] + duplicates)
        }
    }

    return res
}

// y = kx + b
// for 2 points: (x1, y1), (x2, y2) we have a system:
// y1 = kx1 + b
// y2 = kx2 + b
// y1 - y2 = kx1 + b - (kx2 + b) = k*(x1 - x2)
// k = (y1 - y2) / (x1 - x2)
// 
// we return the numerator and denominator of the p/q = ((y1 - y2) / gcd) / ((x1 - x2) / gcd)
// we do this in order to normalize the fraction: here is an example
// 2/3 and 10/15, gcd(2,3) = 1, gcd(10,15) = 5
// 2/3 = 10/5 = 10/5/15/5 = 2/3
func getSlope(a, b []int) (int64, int64) {
    deltaX, deltaY := int64(a[0] - b[0]), int64(a[1] - b[1])

    if deltaX < 0 {
        deltaX = -deltaX
        deltaY = -deltaY
    }

    g := gcd(deltaX, deltaY)
    return deltaX / g, deltaY / g
}

func gcd(a, b int64) int64 {
    for b != 0 {
        a, b = b, a%b
    }

    return a
}

func max(a, b int) int {
    if a < b {
        return b
    }

    return a
}
