// we do this in order to normalize the fraction: here is an example
// 2/3 and 10/15, gcd(2,3) = 1, gcd(10,15) = 5
// 2/3 = 10/5 = 10/5/15/5 = 2/3
func getSlope(a, b []int) (int64, int64) {
    deltaX, deltaY := int64(a[0] - b[0]), int64(a[1] - b[1])

    if deltaX < 0 {
        deltaX = -deltaX
        deltaY = -deltaY
    }

    g := gcd(deltaX, deltaY)
    return deltaX / g, deltaY / g
}

func gcd(a, b int64) int64 {
    for b != 0 {
        a, b = b, a%b
    }

    return a
}

func max(a, b int) int {
    if a < b {
        return b
    }

    return a
}
