class Solution {
    public int maxPoints(int[][] points) {
        if(points.length<3) return points.length;
        int maxres =0;
        //遍历每个点，求其和其他点组成的直线斜率
        for(int i =0; i< points.length; i++){
            int same = 0;//是重复的点
            int temp_max =0;//在某条直线上的最多点数（局部最大值）;每换一个点，就归零
            HashMap<String,Integer> map = new HashMap<>();//存该条线上的斜率和个数
            for(int j= 0; j< points.length; j++){
                if(i!=j){//不能自己和自己
                    int dx = points[i][0] - points[j][0];//points[i][0]第i个点的，index为0的数值，即x值。
                    int dy = points[i][1] - points[j][1];
                    if(dx==0 && dy==0){
                        same++;
                        continue;
                    }
                    //若有斜率，进行约分
                    int gcd = gcd(dx,dy);
                    String key = dx/gcd +"/" + dy/gcd;
                    map.put(key,map.getOrDefault(key,0)+1);
                    temp_max = Math.max(temp_max, map.get(key));
                }               
            }
            //same 代表和当前的点重复的点, 1代表当前点
            maxres = Math.max(maxres,temp_max+same+1);
        }
        return maxres;
    }

    public int gcd(int a, int b){
        while(b!=0){
            int temp = a%b;
            a=b;
            b=temp;
        }
        return a;
    } 

}
