class Solution {
    public int maxPoints(int[][] points) {
        Map<String,Set<int[]>> count = new HashMap<String,Set<int[]>>();
        for(int i = 0; i < points.length; i++) {
            for(int j = i+1; j < points.length; j++) {
                if (points[i][0] == points[j][0]) {
                    String key = String.format("x-%d",points[j][0]);
                    if(!count.containsKey(key)) {
                        count.put(key, new HashSet<int[]>());
                    }
                    count.get(key).add(points[i]);
                    count.get(key).add(points[j]);
                } else {
                    String key = getFunc(points[i],points[j]);
                    if(!count.containsKey(key)) {
                        count.put(key, new HashSet<int[]>());
                    }
                    count.get(key).add(points[i]);
                    count.get(key).add(points[j]);
                }
            }
        }
        int max = points.length > 0 ? 1 : 0;
        for(Set<int[]> values : count.values()) {
            max = Math.max(max,values.size());
        }
        return max;
    }
    private String getFunc(int[] p1, int[] p2) {
        int aNum = p1[1]-p2[1];
        int aDen = p1[0]-p2[0];
        int gcdA = gcd(aNum,aDen);
        aNum /= gcdA;
        aDen /= gcdA;
        int bNum = (aDen*p1[1]) - (aNum*p1[0]);
        int bDen = aDen;
        int gcdB = gcd(bNum,bDen);
        bNum /= gcdB;
        bDen /= gcdB;
        return String.format("%d/%d-%d/%d",aNum,aDen,bNum,bDen);
    }
    private int gcd(int a, int b) {
        if (b==0) return a;
        else return gcd(b,a%b);
    }
}
