Idea is just check every point triplet whether they form a 0 area triangle. As a result, no float introduced. It is O(n^3) but still beats 99.75% which is quite strange...

```
struct myHash {
    size_t operator()(const Point& p) const {
        return hash<int>()(p.x)^hash<int>()(p.y);
    }
};

struct myEq {
    bool operator()(const Point& p1, const Point& p2) const {
        return p1.x == p2.x && p1.y == p2.y;
    }
};

class Solution {
    inline long long area2(int i, int j, int k, vector<pair<Point,int>>& points) {
        return static_cast<long long>(points[i].first.x)*static_cast<long long>(points[j].first.y)
              -static_cast<long long>(points[i].first.y)*static_cast<long long>(points[j].first.x)
              +static_cast<long long>(points[j].first.x)*static_cast<long long>(points[k].first.y)
              -static_cast<long long>(points[j].first.y)*static_cast<long long>(points[k].first.x)
              +static_cast<long long>(points[k].first.x)*static_cast<long long>(points[i].first.y)
              -static_cast<long long>(points[k].first.y)*static_cast<long long>(points[i].first.x);
    }
public:
    int maxPoints(vector<Point>& pts) {
        unordered_map<Point, int, myHash, myEq> ps;
        for (auto& p : pts) ps[p]++;
        int res = 0, n = ps.size();
        if (n < 3) return pts.size();
        vector<pair<Point, int>> points(ps.begin(), ps.end());
        for (int i = 0; i < n-2; ++i)
            for (int j = i+1; j < n-1; ++j) {
                int count = points[i].second+points[j].second;
                res = max(res, count);
                for (int k = j+1; k < n; ++k) 
                    if (area2(i, j, k, points) == 0) {
                        count += points[k].second;
                        res = max(res, count);
                    }
            }
        return res;
    }
};
