class Solution {
public:

    int maxPoints(vector<vector<int>>& points) {
        if(points.size()==1)
        {
            return 1;
        }
        // map :for calculating perpendicular line
        unordered_map<int,int>mp;
        for(int i=0;i<points.size();i++)
        {
            mp[points[i][0]]++;
        }
        //max_xx will store maximum points in a perpendicular line
        int max_xx=INT_MIN;
        for(auto it:mp)
        {
            if(it.second>max_xx)
            max_xx=it.second;
        }
        //sort :for getting all points in increasing order by x-axis
        sort(points.begin(),points.end());
        int count=1;
        //calculate slope for non perpendicular line
        int slope;
        //intial slope will be slope of first non perpendicular line
        for(int i=0;i<points.size()-1;i++)
        {
            if(points[i+1][0]-points[i][0]==0)
            continue;
            else{
            slope=(points[i+1][1]-points[i][1])/(points[i+1][0]-points[i][0]);
            break;
            }
        }
        //slope2 is for comparison of other's slope to check whether they lies in same line.
        int slope2;
        for(int i=0;i<points.size()-1;i++)
        {
            if(points[i+1][0]-points[i][0]==0)
            continue;
            slope2=(points[i+1][1]-points[i][1])/(points[i+1][0]-points[i][0]);
            if(slope2!=slope)
            {
                slope=slope2;
            }
            //if both consecutive slopes are same increase the count
           else if(slope2==slope)
            {

                count++;
            }
        }
        //return maximum of perpendicular lines or normal lines(count)
        return max(max_xx,count);
    }
};

