class Solution:
    def maxPoints(self,points):
        d = {}
        for i in range(len(points)):
            for j in range(i + 1, len(points)):
                if points[j][0] - points[i][0] != 0:
                    slope = (points[j][1] - points[i][1]) / (points[j][0] - points[i][0])
                else:
                    slope = 0
                if slope not in d:
                    d[slope] = []
                d[slope] += ([points[j]])
                d[slope] += ([points[i]])

        ans = 1

        for item in d:

            a = d[item]
            a = sorted(a)
            a = list(a for a, _ in itertools.groupby(a))

            if item != 0:
                m = item

                for it in a:
                    cache = 1
                    for ite in a:
                        if ite != it:
                            y = ite[1]
                            x = ite[0]
                            if  it[1] == (m * it[0]) - (m * x) + y:
                                cache += 1

                    ans = max(ans, cache)
            else:

                x = {}
                y = {}
                for it in a:

                    if it[0] in x:
                        x[it[0]] += 1
                    else:
                        x[it[0]] = 1
                    if it[1] in y:
                        y[it[1]] += 1
                    else:
                        y[it[1]] = 1
                x = list(x.values())
                y = list(y.values())
                x = sorted(x, reverse=True)
                y = sorted(y, reverse=True)
                c = max(y[0], x[0])
                ans = max(ans, c)
        return ans

