class Solution {
public:
    int totalNQueens(int n) {
        int result=0;
        vector<int> solution(n);
        help(n, 0, solution, result);
        return result;
    }
    /** n:#-of-rows   row:current-#-of-row  solution:store-n-row's-col-index result:final result**/
    void help(int n, int row, vector<int>& solution, int& result){
        for(int i=0; i<n; i++){
            if(check(i, row, solution)){
                if(row+1==n){
                    result++;
                    continue;
                }
                solution[row]=i;
                help(n, row+1, solution, result);
            }
        }
    }
    /*** check the cur-col-choise is valid or not **/
    bool check(int col, int row, vector<int>& solution){
        for(int i=0; i<row; i++){
            if(col==solution[i] || abs(col-solution[i])==abs(row-i))  
                return false;
        }
        return true;
    }
};

