class Solution {
public:
    int totalNQueens(int n) {
        int cnt = 0;
        vector<int> curPos;

        totalNQueens(n, curPos, &cnt);

        return cnt;
    }

    bool isColValid(vector<int>& curPos, int curCol) {
        for (int row = 0; row < curPos.size(); row++) {
            if (curPos[row] == curCol) return false;
        }
        return true;
    }

    bool isPosValid(vector<int>& curPos, int curRow, int curCol) {
        for (int row = 0; row < curPos.size(); row++) {
            if (abs(curPos[row] - curCol) == abs(row - curRow) ) return false;
        }
        return true;
    }

    void totalNQueens(int n, vector<int>& curPos, int* cnt) {
        // BASE CASE
        if (curPos.size() == n) {
            *cnt += 1;
            return;
        }

        for (int col = 0; col < n; col++) {
            if (!isColValid(curPos, col)) continue;
            if (!isPosValid(curPos, curPos.size(), col)) continue;
            // Positions valid
            // and to the correct postions
            curPos.push_back(col);
            totalNQueens(n, curPos, cnt);
            // delete this position
            curPos.pop_back();

        }
    }
};
