class Solution {
public:
    int n, res = 0;
	// Check whether placing a new queen at given point is safe or not
    bool isSafe(vector<string> arr, int x, int y){
        for (int row=0; row<x; row++){
            if (arr[row][y]=='Q'){
                return false;
            }
        }

        int row = x;
        int col = y;
        while(row>=0 && col>=0){
            if (arr[row][col]=='Q'){
                return false;
            }
            row--;
            col--;
        }

        row = x;
        col = y;
        while(row>=0 && col<n){
            if (arr[row][col]=='Q'){
                return false;
            }
            row--;
            col++;
        }

        return true;
    }

    void nQueen(vector<string> &ans, int i){
        if (i>=n){
			// count all the possible answers
            res++;
            return;
        }

        for(int k=0;k<n;k++){
            if(isSafe(ans,i,k)){
                ans[i][k] = 'Q';
                nQueen(ans,i+1);
                ans[i][k] = '.';  // backtracking
            }
        }        
    }

    int totalNQueens(int num) {
        n = num;
		// initializing vector with ".......n"
        vector<string> ans(n);
        string temp = "";
        for(int j=0;j<n;j++)
            temp+='.';

        for(int i=0;i<n;i++)
            ans[i] = temp;


        nQueen(ans,0);
        return res;
    }
};
