int backtrackNQueens(vector<vector<int>> &board, int n, int row, int count){
    for(int col = 0; col < n; col++){
        if(board[row][col] == 0){ // 0 is safe
            vector<vector<int>> prevBoard = board; // this is where we will backtrack to
            placeQueen(board, n, row, col);
            if(row == n-1){
                count++;
            } else {
                count = backtrackNQueens(board, n, row + 1, count);
            }
            board = prevBoard; // backtrack the last queen
        } 
    }
    return count;
}

void placeQueen(vector<vector<int>> &board, int n, int row, int col){
    for(int i = 0; i < n; i++){
        board[i][col] = 1; // fill vertically
        board[row][i] = 1; // fill horizontally 
        int j = row + col - i; // fill the NE diagonal 
        if(j >=0 && j < n){
            board[i][j] = 1;
        }
        int k = row - col + i; // fill the SE diagonal
        if(k >=0 && k < n){
            board[k][i] = 1;
        } 
    }
}   
