            if(check(v, i, col)) {
                v[i][col] = 'Q';
                solve(v, col+1);
                v[i][col] = '.';
            }
        }
    }

    int totalNQueens(int n) {
        nl = n;
        vector<string> v(nl, string(nl, '.'));
        solve(v, 0);
        return ans.size();
    }
};
```

